mapper.select:
SELECT COUNT(*) FROM board
AND code = #{searchCode}
AND ${searchType} LIKE || #{searchKeyword} || '%'

mapper.insert:
INSERT INTO board (code, title, content, start_date, end_date, reg_id, created_at, mod_id, updated_at)
VALUES (
        #{code}
        , #{title}
        , #{content}
        , #{startDate}
        , #{endDate}
        , #{regId}
        , SYSDATE
        , #{modId}
        , SYSDATE
       )

mapper.update:
UPDATE board SET
    title = #{title}
    , content = #{content}
    , start_date = #{startDate}
    , end_date = #{endDate}
    , mod_id = #{modId}
    , updated_at = SYSDATE
WHERE id = #{id}
