mapper.select:
SELECT R.review_id AS reviewId, R.user_id AS userId, R.store_id AS storeId, R.rating, R.review_comment AS reviewComment, R.created_at AS createdAt, R.updated_at AS updatedAt, R.username, R."LIKE" AS likeCount, S.store_name AS storeName, R.RESERVE_ID FROM review R LEFT JOIN store S ON R.store_id = S.store_id
AND R.user_id = #{userId}
ORDER BY R.review_id DESC

mapper.update:
UPDATE review
SET rating = #{rating}, review_comment = #{reviewComment}, store_id = #{storeId}
WHERE review_id = #{reviewId}

mapper.select:
SELECT COUNT(*) FROM review
AND store_id = #{searchCode}
AND ${searchType} LIKE '%' || #{searchKeyword} || '%'

SELECT COUNT(*)
FROM review_like
WHERE review_id = #{reviewId} AND user_id = #{userId}

SELECT COUNT(*)
FROM reserve
WHERE user_id = #{userId}
AND store_id = #{storeId}
AND reserve_status = 2

SELECT COUNT(*)
FROM review
WHERE user_id = #{userId}
AND store_id = #{storeId}
AND reserve_id = #{reserveId}

