mapper.update:
UPDATE STORE
STORE_NAME = #{storeName}, ADDRESS = #{address}, STORE_HOURS = #{storeHours}, STORE_STATUS = #{storeStatus}, PHONE = #{phone}, DESCRIPTION = #{description}, FILE_ID = #{fileId}, UPDATED_AT = CURRENT_TIMESTAMP
WHERE STORE_ID = #{storeId}

UPDATE STORE
SET IS_ACTIVE = 1   --가게 활성화
WHERE STORE_ID = #{storeId}

UPDATE STORE
SET IS_ACTIVE = 2   --가게 비활성화(삭제요청)
WHERE STORE_ID = #{storeId}

UPDATE STORE
SET IS_ACTIVE = 3   --가게 삭제처리
WHERE STORE_ID = #{storeId}

mapper.select:
SELECT save_file_name
FROM UPLOAD_FILE
WHERE id = #{fileId}

SELECT FILE_TARGET
FROM UPLOAD_FILE
WHERE id = #{fileId}
