mapper.update:
UPDATE USERS
SET IS_ACTIVE = 0
WHERE USER_ID=#{user_id}

mapper.select:
SELECT USERNAME, NAME, EMAIL
FROM USERS
WHERE EMAIL = #{email} AND USERNAME = #{username} AND NAME = #{name}

mapper.update:
UPDATE USERS
SET PASSWORD=#{password}
WHERE EMAIL=#{email}

UPDATE USERS
SET IS_ACTIVE = 0
WHERE USER_ID = #{userId}

mapper.select:
SELECT IS_ACTIVE
FROM USERS
WHERE USER_ID = #{userId}
